import React, { Component } from 'react'
import defaultBcg from '../images/home-1.jpeg'
import Banner from '../components/Banner'
import Hero from '../components/Hero'
import {Link} from 'react-router-dom'
import {RoomContext} from '../Context'
import StyledHero from '../components/StyledHero'


export default class SingleRoom extends Component {
    constructor(props) {
        super(props);
        this.state ={
            slug:this.props.match.params.slug,
            defaultBcg
        };
    }
    static contextType = RoomContext;
    render() {
        const {getRoom} = this.context;
        const home = getRoom(this.state.slug);
        if (!home){
            return(
           <div className="error">
                <h3>no such room could be found...</h3>
                <Link to="/rooms" className="btn-primary"> back to listings</Link>
            </div>
            );
        }
        const {name,description,size,price,extras,breakfast,pets,images} = room;
        const [mainImg,...defaultImg] = images;
        return (
            <>
            <StyledHero img={mainImg || this.state.defaultBcg}>
                <Banner title={`${name} room`}>
                    <Link to="/rooms" className="btn-primary">
                        back to listings
                    </Link>
                </Banner>
            </StyledHero>
            <section className="single-room">
                <div className="single-room-images">
                    {defaultImg.map((item, index)=>{
                     return <img key={index} src={item} alt={name} />;
                    })}

                </div>
                <div className="single-room-info">
                    <article className="desc">
                        <h3>details</h3>
                        <p>{description}</p>
                    </article>
                    <article className="info">
                        <h3>info</h3>
                        <h6>price : ${price}</h6>
                        <h6>size: ${size} SQFT</h6>
                        {/* <h6>
                            max capacity : {
                                capacity > 1 ?`${capacity} people`: `${capacity}person`
                            }
                        </h6> */}
                        <h6>{pets? "viewings allowed":"no viewings today"}</h6>
                        <h6>{breakfast && "come view today"}</h6>
                    </article>
                </div>
            </section>
            <section className="room-extras">
                <h6>extras</h6>
                <ul className="extras">
                    {extras.map((item,index) =>{
                        return <li key={index}>- {item}</li>
                    })}
                </ul>
            </section>
            </>
        );
    }
}
