import React, { Component } from 'react';
import items from './data';

import { async } from 'q';




const RoomContext = React.createContext();
// <RoomContext.Provider value={}
class RoomProvider extends Component {
    state = {
        homes: [],
        sortedRooms: [],
        // featuredhomes: [],
        loading: true,
        type: 'all',
        capacity:1,
        price:0,
        minPrice:0,
        maxPrice:0,
        maxSize:0,
        minSize:0,
        breakfast: false,
        pets: false,
    };
    // getData
getData = async () =>{
    try {
        let response = await items.getEntries({
            content_type:"resortRoom",
            order:"sys.createdAt"
        });

        // this is to get the contentful data
        // let homes = this.formatData(response.items);
        // below is the version get the local data
        let homes = this.formatData(items);
       
        // let featuredRooms = homes.filter(home => home.featured === true);
      let maxPrice = Math.max(...homes.map(item=> item.price));
      let maxSize = Math.max(...homes.map(item=> item.size));

        this.setState({
            homes,
            // featuredRooms,
            sortedRooms: homes,
            loading: false,
            price:maxPrice,
            maxPrice,
            maxSize,
        });
    } catch (error) {
       console.log(error);
        
    }
}


    componentDidMount() {
        this.getData()
        let homes = this.formatData(items)
        // console.log("homes:", homes)
        let featuredRooms = homes.filter(home => home.featured === true);
      let maxPrice = Math.max(...homes.map(item=> item.price));
      let maxSize = Math.max(...homes.map(item=> item.size));

        this.setState({
            homes,
            featuredRooms,
            sortedRooms: homes,
            loading: false,
            price:maxPrice,
            maxPrice,
            maxSize,
        });
    }

    formatData(items) {
        let tempItems = items.map(item => {
            let id = item.sys.id
            let images = item.fields.images.map(image => image.fields.file.url);

            let home = { ...item.fields,images,id }
            return home;
        });
        return tempItems;
    }
    getRoom = (slug) => {
        let tempRooms = [...this.state.homes];
        const home = tempRooms.find(home=> home.slug===slug);
        return home;
    }
    handleChange = event => {
        const target = event.target;
        const value = target.type === 'checkbox'?target.checked:target.value;
        const name = event.target.name;
        this.setState({
            [name]:value
        },
        this.filterRooms)  
    };
    filterRooms = ()=>{
       let {
           homes, type, capacity, price, minSize, maxSize, breakfast, pets
       } = this.state
    //    all the homes
       let tempRooms =[...homes];
    //    trasform values
    capacity = parseInt(capacity);
    price = parseInt(price);

    // filter by type

       if(type !== 'all'){
           tempRooms = tempRooms.filter(home => home.type === type)
       }

    //    filter by capacity
    if(capacity !==1){
        tempRooms = tempRooms.filter(room => room.capacity >= capacity);
    }
// filter by price
tempRooms=tempRooms.filter( room => room.price <= price);
// filter by size
tempRooms=tempRooms.filter(room => room.size >= minSize && room.size <= maxSize)
// filter by breakfast
if(breakfast){
    tempRooms= tempRooms.filter(room => room.breakfast === true)
}
// filter by pets
if(pets){
    tempRooms= tempRooms.filter(room => room.pets === true)
}


    // change state
       this.setState({
           sortedRooms:tempRooms
       })
        
    };


    render() {
        return (
            <RoomContext.Provider
                value={{ ...this.state, getRoom:this.getRoom, handleChange:this.handleChange }}>
                {this.props.children}
            </RoomContext.Provider>


        );
    }
}


const RoomConsumer = RoomContext.Consumer;

export function withRoomConsumer(Component){
    return function ConsumerWrapper(props){
        return (
        <RoomConsumer>
            {value => <Component {...props} context={value}/>}
        </RoomConsumer>
        );
    };
}



export { RoomProvider, RoomConsumer, RoomContext };
