import React from 'react'
import HomesFilter from './HomesFilter'
import HomesList from './HomesList'
import { withHomeConsumer } from '../Context'
import Loading from './Loading'

function HomesContainer({ context }) {
    const { loading, sortedHomes, homes } = context;
    if (loading) {
        return <Loading />;
    }
    return (

        <>
           
            <HomesFilter homes={homes} />
            <HomesList homes={sortedHomes} />
        </>
    );
}
export default withHomeConsumer(HomesContainer)




